import React, { useState, useEffect } from 'react';
import { initializeApp } from "firebase/app";
import { getDatabase, ref, set, child, get } from "firebase/database";
import './Card.css';

export default function Card({
    key, tag, time, hints, best, goToNextLevelReload
}){

    const [currObjects, setCurrObjects] = useState({});

    const num = localStorage.getItem('num')
    const keyRef = 'users/' + num.toString()

    useEffect(() => {
        readDatabase()
    }, []);

    const readDatabase = () => {
        get(child(dbRef, keyRef)).then((snapshot) => {
          if (snapshot.exists()) {
            const data = snapshot.val()
            setCurrObjects(data)
          } else {
            console.log("No data available");
          }
        }).catch((error) => {
          console.error(error);
        });
      }
    // Database code sourced from https://firebase.google.com/docs/database/web/read-and-write?hl=en&authuser=3#web-version-9_2
    const firebaseConfig = {
        databaseURL: "https://operators-a44ee-default-rtdb.firebaseio.com",
      };

    // Initialize Firebase
    const app = initializeApp(firebaseConfig);
    // Initialize Realtime Database and get a reference to the service
    const database = getDatabase(app);
    const dbRef = ref(database);

    return (
        <div className='card-container'>
            <h2>{tag}</h2>
            <h3>Your time: {time.toString().slice(0, -3)}.{time.toString().slice(1, -2)} seconds</h3>
            {/* <h2>Best time: {best.toString().slice(0, -3)}.{best.toString().slice(1, -2)} seconds</h2> */}
        </div>
    )
}
