import React, { useState, useEffect } from 'react';
import { useNavigate } from "react-router-dom";
import levels from './levels';
import { initializeApp } from "firebase/app";
import { getDatabase, ref, set, child, get } from "firebase/database";
import './App.css';

function App() {
  const [name, setName] = useState('');
  const [submitted, setSubmited] = useState(false);
  const navigate = useNavigate();
  let num = null;

  useEffect(() => {
    if (localStorage.getItem('num')){
      setSubmited(true);
      num = localStorage.getItem('num')
    }
    else {
      num = Math.floor(Math.random()*999999)
    }
    localStorage.setItem('num',num);
    const ref = 'users/' + num.toString()
}, []);




  const submitName = (name) => {
    // Database code sourced from https://firebase.google.com/docs/database/web/read-and-write?hl=en&authuser=3#web-version-9_2
    const firebaseConfig = {
      databaseURL: "https://operators-a44ee-default-rtdb.firebaseio.com",
    };

    // Initialize Firebase
    const app = initializeApp(firebaseConfig);
    // Initialize Realtime Database and get a reference to the service
    const database = getDatabase(app);

    set(ref(database, ref + 'name'), name);
    setSubmited(true)
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Operators</h1>

        {
          !submitted ?
          <div>
            <h3>enter your name</h3>
            <input type="text" value={name} onChange={e => setName(e.target.value)} />
            <button type="button" onClick={() => submitName(name)}>submit</button>
          </div>
          :
          <div>
            <button type="button" onClick={() => navigate('playGame')}>Start a New Game</button>
            <button type="button" onClick={() => navigate('playGame')}>View Past Games</button>
          </div>
        }


      </header>
    </div>
  );
}

export default App;
